mapper.select:
SELECT

FROM hello_blog_auth_user_log

    AND user_id=#{condition.userId} 
    AND ip=#{condition.ip} 
    AND url LIKE '%${condition.url}%' 
    AND parameter LIKE '%${condition.parameter}%' 
    AND device LIKE '%${condition.device}%' 
    AND description LIKE '%${condition.description}%' 
    AND code = #{condition.code} 
    AND browser_name LIKE '%${condition.browserName}%' 
    AND browser_version = #{condition.browserVersion} 
    and DATE_FORMAT(#{condition.createTime},'%Y-%m-%d')=DATE_FORMAT(create_time, '%Y-%m-%d')  

ORDER BY id DESC
